import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import Counter from "./Counter";
const pizzaform = {
  boyut: { kucuk: false, orta: false, buyuk: false },
  hamur: "",
  malzemeler: {
    peperroni: false,
    domates: false,
    biber: false,
    sosis: false,
    misir: false,
    sucuk: false,
    kanadajambonu: false,
    ananas: false,
    cheddar: false,
    jalepano: false,
    kabak: false,
    tavuk: false,
    sogan: false,
    sarimsak: false,
  },
  notlar: "",
};

function FormPage(props) {
  const {
    pizzaType,
    toplamFiyat,
    setToplamFiyat,
    malzemeFiyat,
    setMalzemeFiyat,
  } = props;
  const [specialPizza, setSpecialPizza] = useState(pizzaform);
  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox" && name.includes("boyut")) {
      const guncelBoyut = { ...specialPizza.boyut };
      for (const key in guncelBoyut) {
        guncelBoyut[key] = false;
      }
      guncelBoyut[value] = checked;
      setSpecialPizza({
        ...specialPizza,
        boyut: guncelBoyut,
      });
      console.log(specialPizza);
    }
    // else if (type === "checkbox" && name.includes("malzemeler")) {
    //   const guncelMalzemeler = { ...specialPizza.malzemeler };
