import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import Counter from "./Counter";
import axios from "axios";
import * as yup from "yup";
function FormPage(props) {
  const [isDisabled, setIsDisabled] = useState(true);
  const [formErrors, setFormErrors] = useState({});
  const {
    pizzaType,
    toplamFiyat,
    setToplamFiyat,
    malzemeFiyat,
    setMalzemeFiyat,
    specialPizza,
    setSpecialPizza,
    count,
    setCount,
    pizzaform,
  } = props;
  let schema = yup.object().shape({
    name: yup
      .string()
      .required("En az üç harfli bir isimin olmalı")
      .min(3, "3 karakterden kısa isim olamaz.En az 3 karakter lütfen"),
    email: yup
      .string()
      .email("Eposta adresin yanlış tekrar kontrol eder misin ?")
      .required("Email lazım"),
    password: yup
      .string()
      .required("Bir şifre girmelisin")
      .min(6, "Şifreniz en az 6 karakterli olmalı")
      .matches(/[^0-9]/, "Şifre sadece harf olmamalı sayı da içermeli "),
    agreement: yup
      .boolean()
      .oneOf([true], "Kullanım koşullarını kabul etmelisiniz"),
  });
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox" && name.includes("boyut")) {
      const guncelBoyut = { ...specialPizza.boyut };
      for (const key in guncelBoyut) {
        guncelBoyut[key] = false;
      }
      guncelBoyut[value] = checked;
      setSpecialPizza({
        ...specialPizza,
        boyut: guncelBoyut,
      });
      console.log(specialPizza);
    } else if (type === "checkbox" && name.includes("malzemeler")) {
      const guncelMalzemeler = { ...specialPizza.malzemeler };
      guncelMalzemeler[value] = checked;
