import React from "react";
import { useState } from "react";
function Counter(props) {
  const {
    pizzaType,
    toplamFiyat,
    setToplamFiyat,
    malzemeFiyat,
    setMalzemeFiyat,
    count,
    setCount,
  } = props;

  const handleIncrement = () => {
    setCount(count + 1);
    setToplamFiyat(
      (prevToplamFiyat) => prevToplamFiyat + pizzaType[0].price + malzemeFiyat
    );
  };

  const handleDecrement = () => {
    if (count > 1) {
      setCount(count - 1);
      setToplamFiyat(
        (prevToplamFiyat) => prevToplamFiyat - pizzaType[0].price - malzemeFiyat
      );
    }
  };

  return (
    <div>
      <h1>{count}</h1>
      <button onClick={handleIncrement}>ArtÄ±r (+)</button>
      <button onClick={handleDecrement} disabled={count === 1}>
        Azalt (-)
      </button>
    </div>
  );
}

export default Counter;
