import React from "react";
import { useState } from "react";

function Counter() {
  const {
    pizzaType,
    toplamFiyat,
    setToplamFiyat,
    malzemeFiyat,
    setMalzemeFiyat,
  } = props;
  const [count, setCount] = useState(0);

  const handleIncrement = () => {
    setCount(count + 1);
    setToplamFiyat(toplamFiyat * 2);
    setMalzemeFiyat(malzemeFiyat * 2);
  };

  const handleDecrement = () => {
    setCount(count - 1);
  };

  return (
    <div>
      <h1>Counter: {count}</h1>
      <button onClick={handleIncrement}>ArtÄ±r (+)</button>
      <button onClick={handleDecrement}>Azalt (-)</button>
    </div>
  );
}

export default Counter;
