import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import Counter from "./Counter";

const pizzaform = {
  boyut: { kucuk: false, orta: false, buyuk: false },
  hamur: "",
  malzemeler: {
    peperroni: false,
    domates: false,
    biber: false,
    sosis: false,
    misir: false,
    sucuk: false,
    kanadajambonu: false,
    ananas: false,
    cheddar: false,
    jalepano: false,
    kabak: false,
    tavuk: false,
    sogan: false,
    sarimsak: false,
  },
  notlar: "",
};

function FormPage(props) {
  const {
    pizzaType,
    toplamFiyat,
    setToplamFiyat,
    malzemeFiyat,
    setMalzemeFiyat,
  } = props;
  const [specialPizza, setSpecialPizza] = useState(pizzaform);

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleChange = (e) => {
    const { name, type, checked } = e.target;
    if (type === "checkbox") {
      // Önceki state'i kopyalayın ve belirli checkbox'u güncelleyin
      setSpecialPizza((prevPizza) => ({
        ...prevPizza,
        [name]: checked,
      }));
    } else if (type === "select-one") {
      setSpecialPizza({
        ...specialPizza,
        hamur: e.target.value,
      });
    } else if (type === "text") {
      setSpecialPizza({
        ...specialPizza,
        notlar: e.target.value,
      });
    }
  };
  useEffect(() => {
    console.log("en son halini göster datanın", specialPizza);
  }, [specialPizza]);
  //   useEffect(() => {
  //     fiyatHesapla();
  //   }, [specialPizza, malzemeFiyat, toplamFiyat]);

  function fiyatHesapla() {
    const pizzaFiyat = pizzaType[0].price;
    const malzemeCount = Object.values(specialPizza.malzemeler).filter(
      (value) => value
    ).length;

    const malzemeFiyat = malzemeCount * 5;
    setMalzemeFiyat(malzemeFiyat);
    setToplamFiyat(pizzaFiyat + malzemeFiyat);
  }

  return (
    <>
      <header>
        <div className="form-baslik">
          <h1>Tekonolojik Yemekler</h1>
        </div>
        <div className="form-links">
          <NavLink to="/">Anasayfa-</NavLink>
          <NavLink to="/">Seçenekler-</NavLink>
          <NavLink to="/Formpage">Sipariş Oluştur</NavLink>
        </div>
      </header>
      <div>
        <h3>{pizzaType[0].name}</h3>
        <p>
          {pizzaType[0].price}₺ <span>4.9 (200)</span>{" "}
        </p>

        <p>{pizzaType[0].explanation}</p>
      </div>
      <div className="ilkform-container">
        <form onSubmit={handleSubmit}>
          <div>
            <p>Pizza Boyutu:</p>
            <label>
              Küçük
              <input
                name="boyut.kucuk"
