import React, { useEffect } from "react";
import HomePage from "./components/HomePage";
import Formpage from "./components/FormPage";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { Switch } from "react-router-dom/cjs/react-router-dom.min";
import { useState } from "react";
import axios from "axios";

const initialForm = [
  {
    name: "Position Absoulete Acı Pizza",
    price: 85.5,
    explanation:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum. ",
  },
];
const siparisOzetiForm = {
  name: initialForm[0].name,
  boyut: "",
  malzemeler: "",
};

const pizzaform = {
  boyut: { kucuk: false, orta: false, buyuk: false },
  hamur: "",
  malzemeler: {
    peperroni: false,
    domates: false,
    biber: false,
    sosis: false,
    misir: false,
    sucuk: false,
    kanadajambonu: false,
    ananas: false,
    cheddar: false,
    jalepano: false,
    kabak: false,
    tavuk: false,
    sogan: false,
    sarimsak: false,
  },
  notlar: "",
};
const App = () => {
  const [isDisabled, setIsDisabled] = useState(true);
  const [malzemeFiyat, setMalzemeFiyat] = useState(0);
  const [pizzaType, setPizzaType] = useState(initialForm);
  const [toplamFiyat, setToplamFiyat] = useState(initialForm[0].price);
  const [specialPizza, setSpecialPizza] = useState(pizzaform);
  const [count, setCount] = useState(1);
  const [siparisOzeti, setSiparisOzeti] = useState(siparisOzetiForm);
  const [formErrors, setFormErrors] = useState({});
  const resetForm = () => {
    setSpecialPizza(pizzaform);
    setFormErrors({});
    setIsDisabled(true);
  };
  const handleSubmit = () => {
    if (!isDisabled) {
      axios
        .post("https://reqres.in/api/users", specialPizza)
        .then(function (response) {
          console.log("baba bu data gelmeli ama", response.data);

          const seciliBoyut = Object.keys(response.data.boyut).filter(
            (anahtar) => response.data.boyut[anahtar] === true
          );

          // seciliBoyut dizisini birleştirerek tek bir string haline getir
          const seciliBoyutString = seciliBoyut.join(" ");

          const yeniSiparisOzeti = {
            ...siparisOzeti,
            boyut: seciliBoyutString,
          };
          setSiparisOzeti(yeniSiparisOzeti);
          console.log("sipariş geldi koşşşş", siparisOzeti);
          resetForm();
        })
        .catch(function (error) {
          console.log(error, "error");
          alert("Gönderilemedi");
        });
    }
  };

  return (
    <Router>
      <>
        <div>
          <Switch>
            <Route
              path="/Formpage"
              component={() => (
                <>
                  <Formpage
                    malzemeFiyat={malzemeFiyat}
                    setMalzemeFiyat={setMalzemeFiyat}
                    pizzaType={pizzaType}
                    toplamFiyat={toplamFiyat}
                    setToplamFiyat={setToplamFiyat}
                    specialPizza={specialPizza}
                    setSpecialPizza={setSpecialPizza}
                    count={count}
                    setCount={setCount}
                    pizzaform={pizzaform}
                    isDisabled={isDisabled}
                    setIsDisabled={setIsDisabled}
                    handleSubmit={handleSubmit}
                    formErrors={formErrors}
                    setFormErrors={setFormErrors}
                  />
                </>
              )}
            />{" "}
            <Route path="/" exact component={HomePage} />
          </Switch>
        </div>
      </>
    </Router>
  );
};
export default App;
