import React from "react";
import { useState, useEffect } from "react";
import * as yup from "yup";
import axios from "axios";
import { NavLink } from "react-router-dom";
const pizzaform = {};
function FormPage(props) {
  const { pizzaType } = props;
  const { specialPizza, setspecialPizza } = useState(pizzaform);
  return (
    <>
      <header>
        <div className="form-baslik">
          <h1>Tekonolojik Yemekler</h1>
        </div>
        <div className="form-links">
          <NavLink
            to="/"
            style={({ isActive, isPending }) => {
              return {
                fontWeight: isActive ? "bold" : "",
                color: isPending ? "red" : "black",
              };
            }}
          >
            Anasayfa-
          </NavLink>
          <NavLink
            to="/"
            style={({ isActive, isPending }) => {
              return {
                fontWeight: isActive ? "bold" : "",
                color: isPending ? "red" : "black",
              };
            }}
          >
            Seçenekler-
          </NavLink>
          <NavLink
            to="/Formpage"
            style={({ isActive, isPending }) => {
              return {
                fontWeight: isActive ? "bold" : "",
                color: isPending ? "red" : "black",
              };
            }}
          >
            Sipariş Oluştur
          </NavLink>
        </div>
      </header>
      <div>
        <h3>{pizzaType[0].name}</h3>
        <p>
          {pizzaType[0].price}₺ <span>4.9 (200)</span>{" "}
        </p>

        <p>{pizzaType[0].explanation}</p>
      </div>
      <div className="ilkform-container">
        <form onSubmit={handleSubmit}>
          <div>
            <p>Pizza Boyutu:</p>
            <label>
              Küçük
              <input
                type="checkbox"
                value="kucuk"
                checked={pizzaSize.includes("kucuk")}
                onChange={handleChange}
              />
            </label>
            <label>
              Orta
              <input
                type="checkbox"
                value="orta"
                checked={pizzaSize.includes("orta")}
                onChange={handleChange}
              />
            </label>
            <label>
              Büyük
              <input
                type="checkbox"
                value="buyuk"
                checked={pizzaSize.includes("buyuk")}
                onChange={handleChange}
              />
            </label>
          </div>
          <div>
            <label>
              Hamur Kalınlığı:
              <select
                value={doughType}
                onChange={(e) => setDoughType(e.target.value)}
              >
                <option value="">Seçiniz</option>
                <option value="ince">İnce Hamur</option>
                <option value="normal">Normal Hamur</option>
                <option value="kalin">Kalın Hamur</option>
              </select>
            </label>
          </div>
        </form>
      </div>
    </>
  );
}
export default FormPage;
