import React, { useState } from "react";
import { NavLink } from "react-router-dom";

const pizzaform = {
  boyut: { kucuk: false, orta: false, buyuk: false },
  hamur: "",
};

function FormPage(props) {
  const { pizzaType } = props;
  const [specialPizza, setSpecialPizza] = useState(pizzaform);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Seçilen Pizza Boyutları:", specialPizza.boyut);
    console.log("Seçilen Hamur Kalınlığı:", specialPizza.hamur);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === "checkbox") {
      const updatedPizzaSize = { ...specialPizza.boyut };

      if (checked) {
        updatedPizzaSize[value] = true;
      } else {
        delete updatedPizzaSize[value];
      }

      setSpecialPizza({
        ...specialPizza,
        boyut: updatedPizzaSize,
      });
    } else if (type === "select-one") {
      setSpecialPizza({
        ...specialPizza,
        hamur: value,
      });
    }
  };

  return (
    <>
      <header>
        <div className="form-baslik">
          <h1>Tekonolojik Yemekler</h1>
        </div>
        <div className="form-links">
          <NavLink to="/">Anasayfa-</NavLink>
          <NavLink to="/">Seçenekler-</NavLink>
          <NavLink to="/Formpage">Sipariş Oluştur</NavLink>
        </div>
      </header>
      <div>
        <h3>{pizzaType[0].name}</h3>
        <p>
          {pizzaType[0].price}₺ <span>4.9 (200)</span>{" "}
        </p>

        <p>{pizzaType[0].explanation}</p>
      </div>
      <div className="ilkform-container">
        <form onSubmit={handleSubmit}>
          <div>
            <p>Pizza Boyutu:</p>
            <label>
              Küçük
              <input
                name="boyut.kucuk"
                value="kucuk"
                type="checkbox"
                checked={specialPizza.boyut.kucuk || false}
                onChange={handleChange}
              />
            </label>
            <label>
              Orta
              <input
                name="boyut.orta"
                value="orta"
                type="checkbox"
                checked={specialPizza.boyut.orta || false}
                onChange={handleChange}
              />
            </label>
            <label>
              Büyük
              <input
                name="boyut.buyuk"
                value="buyuk"
                type="checkbox"
                checked={specialPizza.boyut.buyuk || false}
                onChange={handleChange}
              />
            </label>
          </div>
          <div>
            <label>
              Hamur Kalınlığı:
              <select
                name="hamur"
                value={specialPizza.hamur}
                onChange={handleChange}
              >
                <option value="">Seçiniz</option>
                <option value="ince">İnce Hamur</option>
                <option value="normal">Normal Hamur</option>
                <option value="kalin">Kalın Hamur</option>
              </select>
            </label>
          </div>
          <button type="submit">Sipariş Ver</button>
        </form>
      </div>
    </>
  );
}

export default FormPage;
