import React from "react";
import { useState } from "react";

function Counter(props) {
  const {
    pizzaType,
    toplamFiyat,
    setToplamFiyat,
    malzemeFiyat,
    setMalzemeFiyat,
  } = props;
  const [count, setCount] = useState(1);

  const handleIncrement = () => {
    setCount(count + 1);
    setMalzemeFiyat(malzemeFiyat * count);
    setToplamFiyat(toplamFiyat + malzemeFiyat + pizzaType[0].price);
  };

  const handleDecrement = () => {
    setCount(count - 1);
    setMalzemeFiyat(malzemeFiyat / count);
    setToplamFiyat(toplamFiyat - malzemeFiyat - pizzaType[0].price);
  };

  return (
    <div>
      <h1>{count}</h1>
      <button onClick={handleIncrement}>ArtÄ±r (+)</button>
      <button onClick={handleDecrement}>Azalt (-)</button>
    </div>
  );
}

export default Counter;
