import React, { useState } from "react";
import { NavLink } from "react-router-dom";

const pizzaform = {
  boyut: { kucuk: false, orta: false, buyuk: false },
  hamur: "",
  malzemeler: {
    peperroni: false,
    domates: false,
    biber: false,
    sosis: false,
    mısır: false,
    sucuk: false,
    kanadajambonu: false,
    ananas: false,
    cheddar: false,
    jalepano: false,
    kabak: false,
    tavuk: false,
    sogan: false,
    sarımsak: false,
  },
  notlar: "",
};

function FormPage(props) {
  const { pizzaType, toplamFiyat, setToplamFiyat } = props;
  const [specialPizza, setSpecialPizza] = useState(pizzaform);

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === "checkbox") {
      const guncelpizzaBoyut = { ...specialPizza.boyut };
      const pizzaMalzemeleri = { ...specialPizza.malzemeler };

      if (checked) {
        guncelpizzaBoyut[value] = true;
        pizzaMalzemeleri[value] = true;
        console.log("ver bakıyım bana bir consol", pizzaMalzemeleri);
        let trueSayisi = 0;

        for (const malzeme in malzemeler) {
          if (malzemeler[malzeme] === true) {
            trueSayisi++;
          }
        }
      } else {
        delete guncelpizzaBoyut[value];
        delete pizzaMalzemeleri[value];
      }

      setSpecialPizza({
        ...specialPizza,
        boyut: guncelpizzaBoyut,
        malzemeler: pizzaMalzemeleri,
      });
    } else if (type === "select-one") {
      setSpecialPizza({
        ...specialPizza,
        hamur: value,
      });
    } else if (type === "text") {
      setSpecialPizza({
        ...specialPizza,
        notlar: value,
      });
    }
  };

  return (
    <>
      <header>
        <div className="form-baslik">
          <h1>Tekonolojik Yemekler</h1>
        </div>
        <div className="form-links">
          <NavLink to="/">Anasayfa-</NavLink>
          <NavLink to="/">Seçenekler-</NavLink>
          <NavLink to="/Formpage">Sipariş Oluştur</NavLink>
        </div>
      </header>
      <div>
        <h3>{pizzaType[0].name}</h3>
        <p>
          {pizzaType[0].price}₺ <span>4.9 (200)</span>{" "}
        </p>

        <p>{pizzaType[0].explanation}</p>
      </div>
      <div className="ilkform-container">
        <form onSubmit={handleSubmit}>
          <div>
            <p>Pizza Boyutu:</p>
            <label>
              Küçük
              <input
                name="boyut.kucuk"
                value="kucuk"
                type="checkbox"
                checked={specialPizza.boyut.kucuk || false}
                onChange={handleChange}
              />
            </label>
            <label>
              Orta
              <input
                name="boyut.orta"
                value="orta"
                type="checkbox"
                checked={specialPizza.boyut.orta || false}
                onChange={handleChange}
              />
            </label>
            <label>
              Büyük
              <input
                name="boyut.buyuk"
                value="buyuk"
                type="checkbox"
                checked={specialPizza.boyut.buyuk || false}
                onChange={handleChange}
              />
            </label>
          </div>
          <div>
            <label>
              Hamur Kalınlığı:
              <select
                name="hamur"
                value={specialPizza.hamur}
                onChange={handleChange}
              >
                <option value="">Seçiniz</option>
                <option value="ince">İnce Hamur</option>
                <option value="normal">Normal Hamur</option>
                <option value="kalin">Kalın Hamur</option>
              </select>
            </label>
            <div>
              <label>
                Peperroni
                <input
                  name="malzemeler.peperroni"
                  value="peperroni"
                  type="checkbox"
                  checked={specialPizza.malzemeler.peperroni || false}
                  onChange={handleChange}
                />
              </label>
              <label>
                Domates
                <input
                  name="malzemeler.domates"
                  value="domates"
                  type="checkbox"
                  checked={specialPizza.malzemeler.domates || false}
                  onChange={handleChange}
                />
              </label>
              <label>
                Biber
                <input
                  name="malzemeler.biber"
                  value="biber"
                  type="checkbox"
                  checked={specialPizza.malzemeler.biber || false}
                  onChange={handleChange}
                />
              </label>
