import React from "react";
import { useState } from "react";
function Counter(props) {
  const { toplamFiyat, setToplamFiyat, malzemeFiyat, setMalzemeFiyat } = props;
  const [count, setCount] = useState(1);

  const handleIncrement = () => {
    setCount(count + 1);
    setToplamFiyat((prevToplamFiyat) => prevToplamFiyat + 85.5 + malzemeFiyat);
    setMalzemeFiyat((prevMalzemeFiyat) => prevMalzemeFiyat * count);
  };

  const handleDecrement = () => {
    if (count > 1) {
      setCount(count - 1);
      setToplamFiyat(
        (prevToplamFiyat) => prevToplamFiyat - 85.5 - malzemeFiyat
      );
      setMalzemeFiyat((prevMalzemeFiyat) => prevMalzemeFiyat / count);
    }
  };

  return (
    <div>
      <h1>{count}</h1>
      <button onClick={handleIncrement}>ArtÄ±r (+)</button>
      <button onClick={handleDecrement} disabled={count === 1}>
        Azalt (-)
      </button>
    </div>
  );
}

export default Counter;
